sys_script_client.script:
function addOptions(options) {
	for (var i = 0; i < options.length; i++) {
		jslog('Adding option ' + options[i][0]);
		g_form.addOption('u_what_needed', options[i][0], options[i][1]);
	}
}

var hrOpts = [
	['hr1', 'Human Resources 1'],
	['hr2', 'Human Resources 2'],
	['other', 'Other']
];

var facilitiesOpts = [
	['facilities1', 'Facilities 1'],
	['facilities2', 'Facilities 2'],
	['other', 'Other']
];


var legalOpts = [
	['legal1', 'Legal 1'],
	['legal2', 'Legal 2'],
	['other', 'Other']
];

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (isLoading || newValue === '') {
       return;
    }

	g_form.clearOptions('u_what_needed');
	console.log("Awesome");


	switch(newValue) {
		case "hr": 	addOptions(hrOpts); break;
		case "facilities": addOptions(facilitiesOpts); break;
		case "legal": addOptions(legalOpts); 
	}
}
